import { useState } from "react";

const Tenet = () => {
    const [clicked, setClicked] = useState(false);

    const handleClick = () => {
        if (clicked) {
            setClicked(false);
        } else {
            setClicked(true);
        }
    }

    const tenetInfo = (num, text) => {
        if (clicked) {
            return <div>
                <h1>{num}</h1>
                <div>{text}</div>
            </div>
        } else {
            return <div>
                <h1>{num}</h1>
            </div>
        }
    }
    
    return ( 
        <div className="about-tenet">
                <div className="tenet" onClick={handleClick}>
                    {tenetInfo(1, "Stay your blade from the flesh of an innocent")}  
                </div>
                <div className="tenet" onClick={handleClick}>
                    {tenetInfo(2, "Hide in plain sight")}
                </div>
                <div className="tenet" onClick={handleClick}>
                    {tenetInfo(3, "Never Compromise the Brotherhood")}
                </div>
            </div>
     );
}
 
export default Tenet;
